import React, { useState, useRef } from 'react';
import './App.css';
import Header from './Header';
import Footer from './Footer';

function App() {
  const cards = [

  { img: process.env.PUBLIC_URL + '/Headshot.jpeg', title: 'Lydia Patterson', text: 'Lydia Patterson', bio: "Hi I'm Lydia.. contact info...", size: 'N/A', price: 'N/A' },
  { img: process.env.PUBLIC_URL + '/AnUptownPerspective2.jpeg', title: 'An Uptown Perspective 2', text: 'An Uptown Perspective 2', size: '18"x24"', price: '$200'},
  { img: process.env.PUBLIC_URL + '/BlueFrog.jpeg', title: 'Blue Frog', text: 'Blue Frog' , size: '18"x24"', price: '$200'},
  { img: process.env.PUBLIC_URL + '/ComfortInChange.jpeg', title: 'Comfort In Change', text: 'Comfort In Change' , size: '18"x24"', price: '$200'},
  { img: process.env.PUBLIC_URL + '/EndOfSummerFlowers.jpeg', title: 'End Of Summer Flowers', text: 'End Of Summer Flowers' , size: '18"x24"', price: '$200'},
  { img: process.env.PUBLIC_URL + '/FalseLight.jpeg', title: 'False Light', text: 'False Light', size: '18"x24"', price: '$200'},
  { img: process.env.PUBLIC_URL + '/FamiliarFaces.jpeg', title: 'Familiar Faces', text: 'Familiar Faces' , size: '18"x24"', price: '$200'},
  { img: process.env.PUBLIC_URL + '/HabitualRoutines1.jpeg', title: 'Habitual Routines 1', text: 'Habitual Routines 1' , size: '18"x24"', price: '$200'},
  { img: process.env.PUBLIC_URL + '/HabitualRoutines2.jpeg', title: 'Habitual Routines 2', text: 'Habitual Routines 2' , size: '18"x24"', price: '$200'},
  { img: process.env.PUBLIC_URL + '/KingSquareAtNight.jpeg', title: 'King Square At Night', text: 'King Square At Night' , size: '18"x24"', price: '$200'},
  { img: process.env.PUBLIC_URL + '/NoDogsOnTheCouch.jpeg', title: 'No Dogs On The Couch', text: 'No Dogs On The Couch' , size: '18"x24"', price: '$200'},
  { img: process.env.PUBLIC_URL + '/SharingATemporaryHome.jpeg', title: 'Sharing A Temporary Home', text: 'Sharing A Temporary Home' , size: '18"x24"', price: '$200'},
  { img: process.env.PUBLIC_URL + '/SimonAndGarfunkelKids.jpeg', title: 'Simon And Garfunkel Kids', text: 'Simon And Garfunkel Kids', size: '18"x24"', price: '$200'},
  { img: process.env.PUBLIC_URL + '/SillyHeart1.jpeg', title: 'Silly Heart 1', text: 'Silly Heart 1' , size: '18"x24"', price: '$200'},
  { img: process.env.PUBLIC_URL + '/SillyHeart2.jpeg', title: 'Silly Heart 2', text: 'Silly Heart 2' },
  { img: process.env.PUBLIC_URL + '/SillyHeart3.jpeg', title: 'Silly Heart 3', text: 'Silly Heart 3' },
  { img: process.env.PUBLIC_URL + '/SillyHeart4.jpeg', title: 'Silly Heart 4', text: 'Silly Heart 4' },
  { img: process.env.PUBLIC_URL + '/YellowFrog.jpeg', title: 'Yellow Frog', text: 'Yellow Frog' },
  ];
  const [current, setCurrent] = useState(0);
  const [expanded, setExpanded] = useState(false);
  const cardCount = cards.length;

  const goLeft = () => {
    setExpanded(false);
    setCurrent((prev) => (prev === 0 ? cardCount - 1 : prev - 1));
  };
  const goRight = () => {
    setExpanded(false);
    setCurrent((prev) => (prev === cardCount - 1 ? 0 : prev + 1));
  };

  const handleExpand = () => setExpanded((prev) => !prev);

  return (
    <div className="App">
      <div className="animated-bg" aria-hidden="true"></div>
      <div className="content">
        <Header />
        <div className="arrow-scroll-wrapper">
          <div
            className={`scroll-card single${expanded ? ' expanded' : ''}`}
            onMouseEnter={() => setExpanded(true)}
            onMouseLeave={() => setExpanded(false)}
          >
            <img
              src={cards[current].img}
              alt={cards[current].title}
              className={`scroll-img${expanded ? ' shrink' : ''}`}
              onClick={handleExpand}
              style={{ cursor: 'pointer', transition: 'all 0.7s cubic-bezier(.77,0,.18,1)' }}
            />
            {current === 0 && (
              <div className="click-indicator">click the pic for more</div>
            )}
            <div className="arrow-btn-row">
              <button className="arrow-btn left" onClick={goLeft} aria-label="Previous card" disabled={current === 0} style={current === 0 ? { opacity: 0.4, cursor: 'not-allowed' } : {}}>&#8592;</button>
              <div
                className={`scroll-text${expanded ? ' expanded' : ''}`}
                onClick={handleExpand}
                style={{ cursor: 'pointer', transition: 'all 0.7s cubic-bezier(.77,0,.18,1)' }}
              >
                {cards[current].text}
              </div>
              <button className="arrow-btn right" onClick={goRight} aria-label="Next card">&#8594;</button>
            </div>
            <div
              className={`card-details${expanded ? ' show' : ''}`}
              style={{
                maxHeight: expanded ? '200px' : '0',
                opacity: expanded ? 1 : 0,
                transition: 'all 0.7s cubic-bezier(.77,0,.18,1)',
                overflow: 'hidden',
                marginTop: expanded ? '2rem' : '0',
              }}
            >
              {current === 0 ? (
                <div className="card-detail-row">Hi I'm Lydia..</div>
              ) : (
                <>
                  <div className="card-detail-row"><strong>Size:</strong> {cards[current].size}</div>
                  <div className="card-detail-row"><strong>Price:</strong> {cards[current].price}</div>
                </>
              )}
            </div>
            <div className="card-scrollbar">
              {cards.map((_, idx) => (
                <span
                  key={idx}
                  className={"card-scrollbar-dot" + (idx === current ? " active" : "")}
                />
              ))}
            </div>
          </div>
        </div>
      </div>
     {/* <Footer /> */}
    </div>
  );
}

export default App;
