import React from 'react';

function Header() {
  return (
    <header style={{
      background: '#026c20ff',
      color: '#fff',
      padding: '1rem',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
    }}>
      {/* Home image dropdown */}
      <div style={{ position: 'relative', marginRight: '1.5rem' }}>
        <button style={{
          background: 'none',
          border: 'none',
          cursor: 'pointer',
          padding: 0,
          outline: 'none',
        }}>
          <img src={process.env.PUBLIC_URL + '/logo192.png'} alt="Home" style={{ width: 48, height: 48, borderRadius: '50%' }} />
        </button>
        {/* Dropdown menu (hidden by default, simple placeholder) */}
        {/* You can add dropdown logic here later */}
      </div>
      <h1 style={{
        fontFamily: `'Brush Script MT'`,
        fontSize: '3.5rem',
        letterSpacing: '2px',
        textShadow: '2px 2px 8px #00000033',
        margin: 0
      }}>
        Lyd's Art
      </h1>
    </header>
  );
}

export default Header;
