import React, { useState, useRef } from 'react';
import './App.css';
import Header from './Header';
import Footer from './Footer';

function App() {
  const cards = [
    {
      img: process.env.PUBLIC_URL + '/Headshot.png',
      alt: 'Card 1',
      text: 'Card 1 Description',
    },
    {
      img: process.env.PUBLIC_URL + '/FamiliarFaces.png',
      alt: 'Card 2',
      text: 'Card 2 Description',
    },
    {
      img: process.env.PUBLIC_URL + '/FalseLight.ico',
      alt: 'Card 3',
      text: 'Card 3 Description',
    },
  ];
  const [current, setCurrent] = useState(0);
  const cardCount = cards.length;

  const goLeft = () => setCurrent((prev) => (prev === 0 ? cardCount - 1 : prev - 1));
  const goRight = () => setCurrent((prev) => (prev === cardCount - 1 ? 0 : prev + 1));

  return (
    <div className="App">
      <div className="animated-bg" aria-hidden="true"></div>
      <div className="content">
        <Header />
        <div className="arrow-scroll-wrapper">
          <div className="scroll-card single">
            <img src={cards[current].img} alt={cards[current].alt} className="scroll-img" />
            <div className="scroll-text">{cards[current].text}</div>
            <div className="arrow-btn-row">
              <button className="arrow-btn left" onClick={goLeft} aria-label="Previous card">&#8592;</button>
              <button className="arrow-btn right" onClick={goRight} aria-label="Next card">&#8594;</button>
            </div>
          </div>
        </div>
      </div>
      <Footer />
    </div>
  );
}

export default App;
