import React from 'react';
import './App.css';

const Contact = () => (
  <section id="contact" className="contact-section" style={{ display: 'flex', flexDirection: 'column', alignItems: 'center', marginTop: '3rem', gap: '2rem' }}>
    <a 
      href="https://www.instagram.com/lydiapatersonart/" 
      target="_blank" 
      rel="noopener noreferrer"
      style={{ display: 'flex', flexDirection: 'column', alignItems: 'center', color: '#fff', textDecoration: 'none', fontWeight: 500, gap: '1rem', padding: '1rem 2rem', background: '#6d4c1bca', borderRadius: '1rem', boxShadow: '0 2px 12px #0006' }}
    >
      <img src={process.env.PUBLIC_URL + '/InstaLogo.png'} alt="Instagram" style={{ width: '72px', height: '72px', objectFit: 'contain', marginBottom: '1rem' }} />
      <span style={{ fontSize: '1.5rem', letterSpacing: '0.05em' }}>Instagram</span>
    </a>

    {/* Instagram embed for the most recent post */}
    <div style={{ width: '100%', maxWidth: '400px', minHeight: '480px', display: 'flex', justifyContent: 'center', alignItems: 'center', background: '#fffbe6', borderRadius: '1rem', boxShadow: '0 2px 12px #0002', padding: '1rem' }}>
      <iframe
        src="https://www.instagram.com/lydiapatersonart/embed"
        title="Instagram Feed"
        width="100%"
        height="480"
        frameBorder="0"
        scrolling="no"
        allowtransparency="true"
        style={{ border: 'none', borderRadius: '1rem', width: '100%', minHeight: '480px' }}
      ></iframe>
    </div>
  </section>
);

export default Contact;
