import React from 'react';
import './App.css';

const Home = () => (
  <section id="home" className="home-section" style={{ display: 'flex', flexDirection: 'column', alignItems: 'center', marginTop: '2rem' }}>
    <img 
      src={process.env.PUBLIC_URL + '/Headshot.jpeg'} 
      alt="Lydia Paterson Headshot" 
      style={{ width: '220px', height: '220px', objectFit: 'cover', borderRadius: '50%', boxShadow: '0 4px 24px #0004', marginBottom: '2rem' }}
    />
    <div className="biography-text" style={{ maxWidth: '600px', textAlign: 'center', fontSize: '1.25rem', color: '#6d4c1b',background: 'linear-gradient(90deg, #f7ecd0 0%, #f3e3b3 100%)', padding: '2rem', borderRadius: '1rem', boxShadow: '0 2px 12px #0006' }}>
      Lydia Paterson is a contemporary artist whose work explores the intersection of memory, nature, and emotion. Her vibrant paintings and mixed media pieces evoke a sense of wonder and nostalgia, drawing inspiration from the landscapes and people of her hometown. Lydia's creative journey began in childhood, sketching scenes from her family garden and experimenting with color. She studied fine arts at university, where she developed her unique style blending realism with abstract elements. Today, Lydia continues to push boundaries, exhibiting her art in galleries and sharing her process with a growing online community. When not painting, she enjoys hiking, reading poetry, and collaborating with fellow artists.
    </div>
  </section>
);

export default Home;
