import React, { useState, useRef, useEffect } from 'react';
import HomeIcon from './HomeIcon';

function Header() {
  const [dropdownOpen, setDropdownOpen] = useState(false);
  const dropdownRef = useRef(null);

  useEffect(() => {
    function handleClickOutside(event) {
      if (dropdownRef.current && !dropdownRef.current.contains(event.target)) {
        setDropdownOpen(false);
      }
    }
    document.addEventListener('mousedown', handleClickOutside);
    return () => document.removeEventListener('mousedown', handleClickOutside);
  }, []);

  return (
    <header style={{
      background: '#026c20ff',
      color: '#fff',
      padding: '1rem',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      position: 'relative',
    }}>

      <div ref={dropdownRef} style={{ position: 'absolute', left: '1.5rem', display: 'flex', alignItems: 'center', height: '100%' }}>
        <button
          onClick={() => setDropdownOpen((open) => !open)}
          style={{
            background: 'none',
            border: 'none',
            cursor: 'pointer',
            padding: 0,
            outline: 'none',
            display: 'flex',
            alignItems: 'center',
          }}
          aria-label="Home menu"
        >
          <HomeIcon size={40} color="#fff" />
        </button>

        {/** when dropdown is clicked display little menu for */}

        {dropdownOpen && (
          <div style={{
            position: 'absolute',
            top: '110%',
            left: 0,
            background: '#fff',
            color: '#026c20ff',
            borderRadius: '8px',
            boxShadow: '0 2px 8px rgba(0,0,0,0.15)',
            minWidth: '140px',
            zIndex: 10,
            padding: '0.5rem 0',
          }}>
            <a href="#about" style={{ display: 'block', padding: '0.5rem 1rem', textDecoration: 'none', color: '#026c20ff', fontWeight: 500 }}>About Me</a>
            <a href="#work" style={{ display: 'block', padding: '0.5rem 1rem', textDecoration: 'none', color: '#026c20ff', fontWeight: 500 }}>My Work</a>
          </div>
        )}
      </div>

      <h1 style={{
        fontFamily: `'Brush Script MT'`,
        fontSize: '3.5rem',
        letterSpacing: '2px',
        textShadow: '2px 2px 8px #00000033',
        margin: 0
      }}>
        Lyd's Art
      </h1>
    </header>
  );
}

export default Header;
