import React, { useState } from 'react';
import './App.css';
import Header from './Header';
import Footer from './Footer';
import Home from './Home';
import Artwork from './Artwork';
import Contact from './Contact';
import Cart from './Cart';

// Import the functions you need from the SDKs you need
import { initializeApp } from "firebase/app";
import { getAnalytics } from "firebase/analytics";
// TODO: Add SDKs for Firebase products that you want to use
// https://firebase.google.com/docs/web/setup#available-libraries

// Your web app's Firebase configuration
// For Firebase JS SDK v7.20.0 and later, measurementId is optional
const firebaseConfig = {
  apiKey: "AIzaSyDrys46ynoCxzecrEJKJpdlUiP3KGzgoE4",
  authDomain: "lydiapatersonart.firebaseapp.com",
  projectId: "lydiapatersonart",
  storageBucket: "lydiapatersonart.firebasestorage.app",
  messagingSenderId: "501542056109",
  appId: "1:501542056109:web:fd01863a2d20e02eb01624",
  measurementId: "G-3RSWGKMHRN"
};

// Initialize Firebase
const app = initializeApp(firebaseConfig);
const analytics = getAnalytics(app);


function MainApp() {
  const [page, setPage] = useState('home');
  const [cartData, setCartData] = useState(null);

  // Handler for navigation
  const handleNav = (target, data) => {
    setPage(target);
    if (target === 'cart' && data) {
      setCartData(data);
    }
  };

  return (
    <div className="App">
      <div className="animated-bg" aria-hidden="true"></div>
      <div className="content">
        <Header onNavigate={handleNav} />
        {page === 'home' && <Home />}
        {page === 'artwork' && <Artwork onReserve={data => handleNav('cart', data)} />}
        {page === 'contact' && <Contact />}
        {page === 'cart' && <Cart card={cartData} />}
        <Footer/>
      </div>
    </div>
  );
}

function App() {
  return <MainApp />;
}

export default App;
